import React,{useState,useEffect} from 'react'

import { Button } from 'reactstrap'
import {io} from 'socket.io-client'
import axios from 'axios'

console.log('refreshed')
const Game = (props) => {
    const [squares,setSquares]=useState(null)
    const [isX,setIsX]=useState(null)
    const [socket,setSocket]=useState()
    const [userCredentials,setUserCredentials]=useState(null)
    const [opponent,setOpponent]=useState(null)
    const {name,channel}=props.location.state
    if(channel>100 || channel<=0 || channel===null || name===null){
        props.history.push({pathname:'/',state:'Please Enter Valid Channel No'})
    }

    const leave=()=>{
        props.history.push('/')
    }

    useEffect(() => {
        if(props.history===null || channel===null)
            return
        axios.get(`http://localhost:5000/credentials?channel=${channel}`)
        .then(res=>{
            if(res.data.msg){
                props.history.push({pathname:'/',state:'Channel is Full. Join Another Channel'})
            }
            else{
                setUserCredentials(res.data.info)
            }
        })
        .catch(err=>console.log(err))
        return () => {
            setUserCredentials(null)
        }
    }, [])

    useEffect(()=>{
        const s=io('http://localhost:5000')
        setSocket(s)
        return ()=>{
            setSocket(null)
        }
    },[])

    useEffect(() => {
        if(socket===null)
            return
        socket.emit('get-data',{name,userCredentials,channel})

        socket.on('giveOwnSocketId',()=>{
            socket.emit('takeSocketId',socket)
        })
        socket.on('opponent')
        return () => {
            
        }
    }, [socket])

    
    const c=(ind)=>{
        console.log('click'+ind)
    }
    var x=document.querySelectorAll(".demo")
    x.forEach((elem,index)=>{
        elem.addEventListener('click',()=>c(index))
    })
    
    console.log(props)
    // props.location.state={state9:'suprise'}
    const handle1=()=>{
        props.history.push({pathname:'/',state:{...props.location.state}})
    }
    const handle2=()=>{
        props.history.push({pathname:'/game',state:{...props.location.state,state2:'this is 2',state1:'this is 3'}})
    }
    const headerStyle={
        margin: "30px auto",
        fontWeight:'bold',
        fontSize:'40px',
        textDecoration:'underline' 
      }
    return (
      <div className="App">
          {/* <button onClick={handle1}>goto home</button>
          <button onClick={handle2}>goto game</button> */}

        {/* header  */}
        <h1 className="center" style={headerStyle}>
          Tic-Tac-Toe   <span className="glyphicon glyphicon-play-circle"></span>
        </h1>

        {/* board  */}
        <div className="Board">
        <div className="chance">You : {name!==null && name}</div>
        <div className="chance">Opponent : {opponent!==null && opponent}</div>
        <div className="chance">Your Character : {userCredentials!==null && userCredentials.userIsX?'X':'O'}</div>
        <div className="chance">Now Chance : </div>

        {/* board */}
         <div className="Board-row">
            <Button className="Square" onClick={props.onTouch} color="dark" outline> {squares===null?null:squares[0]} </Button>
            <Button className="Square" onClick={props.onTouch} color="dark" outline> {squares===null?null:squares[1]} </Button>
            <Button className="Square" onClick={props.onTouch} color="dark" outline> {squares===null?null:squares[2]} </Button>
         </div> 
         <div className="Board-row">
            <Button className="Square" onClick={props.onTouch} color="dark" outline> {squares===null?null:squares[3]} </Button>
            <Button className="Square" onClick={props.onTouch} color="dark" outline> {squares===null?null:squares[4]} </Button>
            <Button className="Square" onClick={props.onTouch} color="dark" outline> {squares===null?null:squares[5]} </Button>
         </div>    
         <div className="Board-row">
            <Button className="Square" onClick={props.onTouch} color="dark" outline> {squares===null?null:squares[6]} </Button>
            <Button className="Square" onClick={props.onTouch} color="dark" outline> {squares===null?null:squares[7]} </Button>
            <Button className="Square" onClick={props.onTouch} color="dark" outline> {squares===null?null:squares[8]} </Button>
         </div> 
        </div>
        <div className="mt-5 mx-auto" style={{display:'flex',justifyContent:'space-between',maxWidth:'15%'}}>
            <Button size="lg" onClick={leave} color="primary" >Leave</Button>
            <Button size="lg" color="primary" >Reset</Button>
        </div>
        {/* <div className="Board-row">
            <button className="demo">0</button>
        </div>
        <div className="Board-row">
            <button className="demo">3</button>
            <button className="demo">4</button> 
            <button className="demo">5</button>
        </div>   */}
    </div>
    );
} 
 
export default Game
