import React from 'react'
import Board from './Board'

console.log('refreshed')
const Game = (props) => {
    if(document.querySelector(".demo").innerHTML)
    document.querySelector(".demo").innerHTML="hello"
    console.log(props)
    // props.location.state={state9:'suprise'}
    const handle1=()=>{
        props.history.push({pathname:'/',state:{...props.location.state}})
    }
    const handle2=()=>{
        props.history.push({pathname:'/game',state:{...props.location.state,state2:'this is 2',state1:'this is 3'}})
    }
    const headerStyle={
        margin: "30px auto",
        fontWeight:'bold',
        fontSize:'40px',
        textDecoration:'underline' 
      }
    return (
      <div className="App">
          <button onClick={handle1}>goto home</button>
          <button onClick={handle2}>goto game</button>
        <h1 className="center" style={headerStyle}>
          Tic-Tac-Toe   <span className="glyphicon glyphicon-play-circle"></span>
        </h1>
        <Board />

        <div className="Board-row">
            <button className="demo">0</button>
        </div>
        {/* <div className="Board-row">
            <button className="demo">3</button><button className="demo">4</button><button className="demo">5</button>
        </div> */}
    </div>
    );
}

export default Game
